import React, { useState } from 'react';
import { signInWithEmailAndPassword, signInWithPopup, GoogleAuthProvider } from 'firebase/auth';
import { auth } from '../firebase/config';

const Login = ({ onLogin, switchToRegister }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      onLogin();
    } catch (error) {
    //   setError(error.message);
    const errorMessage = getCustomErrorMessage(error.code);
    setError(errorMessage); // Display the custom error message
    }
  };
  const getCustomErrorMessage = (errorCode) => {
    const errorMessages = {
      "auth/invalid-credential": "USER NOT FOUND",
      "auth/wrong-password": "Incorrect password. Please try again.",
      "auth/invalid-email": "Invalid email address. Please check and try again.",
      "auth/too-many-requests": "Too many attempts. Please try again later.",
      // Add more custom messages as needed
    };
    return errorMessages[errorCode] || "An unexpected error occurred. Please try again.";
  };

  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
      onLogin();
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="container">
      <h1>Login</h1>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      <button onClick={handleGoogleLogin} className="google-btn">
        <img src="data:image/svg+xml;base64," alt="Google logo" />
        Sign in with Google
      </button>
      <div className="switch-form">
        Don't have an account?{' '}
        <span onClick={switchToRegister}>Register here</span>
      </div>
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default Login;

